// Invoice.js
import React from 'react';
import { Page, Text, View, Document, StyleSheet,Image } from '@react-pdf/renderer';

const styles = StyleSheet.create({
  page: {
    padding: 10,
    fontSize: 10,
    width: 226.77, // 80mm in points
  },
  section: {
    marginBottom: 6,
  },
  header: {
    fontSize: 14,
    textAlign: 'center',
    marginBottom: 4,
  },
  header1: {
    fontSize: 8,
    textAlign: 'center',
    marginBottom: 4,
  },
  line: {
    borderBottom: '1pt dashed gray',
    marginVertical: 4,
  },
  itemRow: {
    marginBottom: 4,
  },
  itemText: {
    fontSize: 10,
  },
  total: {
    fontSize: 12,
    fontWeight: 'bold',
    textAlign: 'right',
    marginTop: 8,
  },
  logo: {
    width: 60, // or smaller if needed
    height: 60,
    marginBottom: 5,
    alignSelf: 'center',
  },
  rowBetween: {
    flexDirection: 'row',
    justifyContent: 'space-between',
  },
  
  
});

const Invoice = ({ orderData }) => {
    const headerHeight = 180; // logo, title, customer info, total etc.
    const itemHeight = 26; // average per item
    const cartHeight = orderData.cart.length * itemHeight;
    const totalHeight = headerHeight + cartHeight + 60;

  return (
    <Document>
      <Page size={[210, totalHeight]} style={styles.page}>
      <View wrap={false}>
      <Image style={styles.logo} src="data:image/png;base64," alt="Company Logo" />
    
        <View style={styles.section}>
        
            
          <Text style={styles.header}>GRS Mobile</Text>
          <Text style={styles.header1}>Main street, Pelmadulla</Text>
          <Text style={styles.header1}>+94 76 966 8478</Text>
        </View>
      


        <View style={styles.section}>
        <Text>Customer: {orderData.customer ? `${orderData.customer.first_name} ${orderData.customer.last_name}` : 'N/A'}</Text>
          <Text>Date: {new Date().toLocaleDateString()}</Text>
        </View>
        <View style={styles.line} />

        <View style={styles.section}>
          {orderData.cart.map((item, index) => (
           <View key={index} style={styles.itemRow}>
           <Text style={styles.itemText}>{item.name}</Text>
           <View style={styles.rowBetween}>
             <Text style={styles.itemText}>Qty: {item.pivot.quantity} × {item.price}</Text>
             <Text style={styles.itemText}>Rs.{(item.pivot.quantity * item.price).toFixed(2)}</Text>
           </View>
         </View>
         
          ))}
        </View>

        <View style={styles.line} />
        <Text style={styles.total}>Total: Rs. {orderData.amount}</Text>
        <View style={styles.line} />
        <View style={styles.line} />

        <View style={{ marginTop: 8 }}>
          <Text style={{ textAlign: 'center' }}>Thank you! Come Again.</Text>
         

        </View>
        <View style={{ marginTop: 6 }}>
         
          <Text style={{ textAlign: 'center',fontSize: 8  }}>Developed by crow.lk</Text>
          <Text style={{ textAlign: 'center',fontSize: 8  }}>contact +94 77 437 9390</Text>

        </View>
        </View>
      </Page>
    </Document>
  );
};

export default Invoice;
